INSERT INTO question_bank (subject, topic, questionText, options, correctAnswer) VALUES
('Physics', 'Kinematics', 'What is the SI unit of velocity?', '["m/s", "km/h", "m^2", "kg"]', 'm/s', 'Velocity is measured as distance per unit time.'),
('Physics', 'Kinematics', 'Which quantity is a vector?', '["Speed", "Distance", "Velocity", "Time"]', 'Velocity', 'Velocity has both magnitude and direction.'),
('Physics', 'Kinematics', 'What does the slope of a distance-time graph represent?', '["Speed", "Acceleration", "Displacement", "Velocity"]', 'Speed', 'Slope of a distance-time graph gives speed.'),
('Physics', 'Kinematics', 'What is the acceleration of an object at rest?', '["0", "1", "9.8", "Depends on mass"]', '0', 'An object at rest has zero acceleration.'),
('Physics', 'Kinematics', 'Which graph shows uniform motion?', '["Straight line", "Curve", "Parabola", "Zigzag"]', 'Straight line', 'Uniform motion is represented by a straight line.'),
('Physics', 'Kinematics', 'What is the formula for average speed?', '["Total distance / total time", "Speed × time", "Distance × time", "Acceleration × time"]', 'Total distance / total time', 'Average speed is total distance divided by total time.'),
('Physics', 'Kinematics', 'Which of the following is not a kinematic quantity?', '["Displacement", "Velocity", "Acceleration", "Force"]', 'Force', 'Force is a dynamic quantity, not kinematic.'),
('Physics', 'Kinematics', 'What is the unit of acceleration?', '["m/s", "m/s²", "m", "s"]', 'm/s²', 'Acceleration is measured in meters per second squared.'),
('Physics', 'Kinematics', 'What does negative acceleration indicate?', '["Speeding up", "Slowing down", "Constant speed", "No motion"]', 'Slowing down', 'Negative acceleration means deceleration.'),
('Physics', 'Kinematics', 'Which equation is used in uniformly accelerated motion?', '["v = u + at", "s = ut", "v = s/t", "a = v/t"]', 'v = u + at', 'This equation relates final velocity, initial velocity, acceleration, and time.');
INSERT INTO question_bank (subject, topic, questionText, options, correctAnswer) VALUES
('Physics', 'Laws of Motion', 'Which law explains the relationship between force, mass, and acceleration?', '["Newton\'s First Law", "Newton\'s Second Law", "Newton\'s Third Law", "Law of Gravitation"]', 'Newton\'s Second Law', 'Newton\'s Second Law states F = ma, linking force, mass, and acceleration.'),
('Physics', 'Laws of Motion', 'What is inertia?', '["Resistance to motion", "Resistance to change in motion", "Force of motion", "Speed of motion"]', 'Resistance to change in motion', 'Inertia is the tendency of an object to resist changes in its state of motion.'),
('Physics', 'Laws of Motion', 'Which law states that every action has an equal and opposite reaction?', '["Newton\'s First Law", "Newton\'s Second Law", "Newton\'s Third Law", "Law of Conservation"]', 'Newton\'s Third Law', 'Newton\'s Third Law explains action-reaction force pairs.'),
('Physics', 'Laws of Motion', 'What is the net force on an object moving with constant velocity?', '["Zero", "Equal to mass", "Equal to acceleration", "Infinite"]', 'Zero', 'Constant velocity implies no net force acting on the object.'),
('Physics', 'Laws of Motion', 'Which quantity changes when a force is applied?', '["Mass", "Weight", "Velocity", "Inertia"]', 'Velocity', 'Force causes a change in velocity, resulting in acceleration.'),
('Physics', 'Laws of Motion', 'What is the unit of force?', '["kg", "m/s", "N", "J"]', 'N', 'Force is measured in Newtons (N).'),
('Physics', 'Laws of Motion', 'Which law is also called the law of inertia?', '["Newton\'s First Law", "Newton\'s Second Law", "Newton\'s Third Law", "Law of Gravitation"]', 'Newton\'s First Law', 'Newton\'s First Law describes inertia.'),
('Physics', 'Laws of Motion', 'What is the force required to accelerate a 2 kg object at 3 m/s²?', '["6 N", "5 N", "3 N", "2 N"]', '6 N', 'Using F = ma → 2 × 3 = 6 N.'),
('Physics', 'Laws of Motion', 'Which force opposes motion between two surfaces?', '["Gravitational force", "Friction", "Normal force", "Magnetic force"]', 'Friction', 'Friction resists relative motion between surfaces.'),
('Physics', 'Laws of Motion', 'What happens to acceleration if mass increases and force remains constant?', '["Increases", "Decreases", "Remains same", "Becomes zero"]', 'Decreases', 'From F = ma, if mass increases, acceleration decreases.');
INSERT INTO question_bank (subject, topic, questionText, options, correctAnswer) VALUES
('Physics', 'Work, Energy & Power', 'What is the SI unit of work?', '["Joule", "Watt", "Newton", "Pascal"]', 'Joule', 'Work is measured in Joules, which is equivalent to N·m.'),
('Physics', 'Work, Energy & Power', 'Which of the following is a scalar quantity?', '["Work", "Force", "Displacement", "Velocity"]', 'Work', 'Work has magnitude but no direction, making it scalar.'),
('Physics', 'Work, Energy & Power', 'What is the formula for power?', '["Work × time", "Work / time", "Force × distance", "Energy × time"]', 'Work / time', 'Power is the rate at which work is done.'),
('Physics', 'Work, Energy & Power', 'What is the unit of power?', '["Joule", "Watt", "Newton", "Ampere"]', 'Watt', 'Power is measured in Watts (W).'),
('Physics', 'Work, Energy & Power', 'Which energy is stored in a stretched spring?', '["Kinetic energy", "Potential energy", "Thermal energy", "Chemical energy"]', 'Potential energy', 'A stretched spring stores elastic potential energy.'),
('Physics', 'Work, Energy & Power', 'What is 1 horsepower equal to?', '["746 W", "1000 W", "500 W", "1 kW"]', '746 W', '1 horsepower is approximately 746 watts.'),
('Physics', 'Work, Energy & Power', 'Which of the following is not a form of mechanical energy?', '["Kinetic energy", "Potential energy", "Thermal energy", "Gravitational energy"]', 'Thermal energy', 'Thermal energy is not mechanical; it’s related to heat.'),
('Physics', 'Work, Energy & Power', 'What is the work done when force is perpendicular to displacement?', '["Maximum", "Minimum", "Zero", "Infinite"]', 'Zero', 'Work is zero when force is perpendicular to displacement.'),
('Physics', 'Work, Energy & Power', 'What is the energy possessed by a body due to its motion?', '["Potential energy", "Kinetic energy", "Thermal energy", "Chemical energy"]', 'Kinetic energy', 'Kinetic energy is due to motion.'),
('Physics', 'Work, Energy & Power', 'Which of the following is a renewable source of energy?', '["Coal", "Petroleum", "Solar", "Natural gas"]', 'Solar', 'Solar energy is renewable and sustainable.');
INSERT INTO question_bank (subject, topic, questionText, options, correctAnswer) VALUES
('Physics', 'Gravitation', 'What is the value of acceleration due to gravity on Earth?', '["9.8 m/s²", "10 m/s²", "8.9 m/s²", "9.2 m/s²"]', '9.8 m/s²', 'Standard gravitational acceleration on Earth is approximately 9.8 m/s².'),
('Physics', 'Gravitation', 'Which law governs the force of attraction between two masses?', '["Newton\'s Law of Motion", "Kepler\'s Law", "Newton\'s Law of Gravitation", "Hooke\'s Law"]', 'Newton\'s Law of Gravitation', 'Newton\'s Law of Gravitation defines the force between two masses as proportional to their product and inversely proportional to the square of the distance between them.'),
('Physics', 'Gravitation', 'What happens to gravitational force if the distance between two bodies is doubled?', '["Halved", "Doubled", "Quadrupled", "Reduced to one-fourth"]', 'Reduced to one-fourth', 'Gravitational force is inversely proportional to the square of the distance. Doubling the distance reduces the force to one-fourth.'),
('Physics', 'Gravitation', 'Which quantity remains constant during free fall?', '["Mass", "Weight", "Velocity", "Acceleration"]', 'Acceleration', 'Acceleration due to gravity remains constant during free fall.'),
('Physics', 'Gravitation', 'What is the shape of planetary orbits according to Kepler\'s first law?', '["Circular", "Elliptical", "Parabolic", "Hyperbolic"]', 'Elliptical', 'Kepler\'s first law states that planets move in elliptical orbits with the Sun at one focus.'),
('Physics', 'Gravitation', 'What is the escape velocity from Earth\'s surface?', '["11.2 km/s", "9.8 km/s", "7.9 km/s", "5.6 km/s"]', '11.2 km/s', 'Escape velocity is the minimum speed needed to break free from Earth\'s gravitational pull.'),
('Physics', 'Gravitation', 'Which factor does not affect gravitational force?', '["Mass of objects", "Distance between objects", "Medium between objects", "Gravitational constant"]', 'Medium between objects', 'Gravitational force is independent of the medium between objects.'),
('Physics', 'Gravitation', 'What is the unit of gravitational constant G?', '["Nm²/kg²", "N/kg", "m/s²", "kg/m²"]', 'Nm²/kg²', 'The gravitational constant G has units of Newton meter squared per kilogram squared.'),
('Physics', 'Gravitation', 'What is the weight of a body in space?', '["Same as on Earth", "Zero", "Double", "Half"]', 'Zero', 'Weight depends on gravity, and in space (microgravity), it becomes zero.'),
('Physics', 'Gravitation', 'Which planet has the highest surface gravity?', '["Earth", "Jupiter", "Mars", "Venus"]', 'Jupiter', 'Jupiter has the highest surface gravity due to its large mass.');
INSERT INTO question_bank (subject, topic, questionText, options, correctAnswer) VALUES
('Physics', 'Sound', 'What is the speed of sound in air at room temperature?', '["343 m/s", "300 m/s", "400 m/s", "500 m/s"]', '343 m/s', 'At 20°C, sound travels through air at approximately 343 meters per second.'),
('Physics', 'Sound', 'Which physical quantity determines the pitch of sound?', '["Amplitude", "Frequency", "Wavelength", "Speed"]', 'Frequency', 'Pitch is directly related to the frequency of the sound wave.'),
('Physics', 'Sound', 'What is the unit of frequency?', '["Hz", "dB", "m/s", "J"]', 'Hz', 'Frequency is measured in Hertz (Hz), which is cycles per second.'),
('Physics', 'Sound', 'Which part of the ear helps in detecting sound?', '["Cochlea", "Eardrum", "Pinna", "Auditory nerve"]', 'Cochlea', 'The cochlea converts sound vibrations into nerve impulses.'),
('Physics', 'Sound', 'Which wave property changes when sound moves from air to water?', '["Frequency", "Amplitude", "Speed", "Time period"]', 'Speed', 'Speed of sound increases in denser media like water.'),
('Physics', 'Sound', 'What type of wave is sound?', '["Transverse", "Longitudinal", "Electromagnetic", "Surface"]', 'Longitudinal', 'Sound waves are longitudinal, with particles vibrating parallel to wave direction.'),
('Physics', 'Sound', 'Which frequency range is audible to humans?', '["20 Hz to 20 kHz", "2 Hz to 2 kHz", "200 Hz to 200 kHz", "0.2 Hz to 2 MHz"]', '20 Hz to 20 kHz', 'Humans can hear sounds between 20 Hz and 20,000 Hz.'),
('Physics', 'Sound', 'What is the phenomenon of sound bouncing back called?', '["Refraction", "Diffraction", "Echo", "Absorption"]', 'Echo', 'An echo is a reflected sound wave heard after the original sound.'),
('Physics', 'Sound', 'Which device is used to measure sound intensity?', '["Barometer", "Thermometer", "Sound level meter", "Oscilloscope"]', 'Sound level meter', 'Sound level meters measure sound intensity in decibels.'),
('Physics', 'Sound', 'What causes sound in musical instruments?', '["Electricity", "Vibration", "Heat", "Light"]', 'Vibration', 'Sound is produced by vibrations of strings, membranes, or air columns.');
INSERT INTO question_bank (subject, topic, questionText, options, correctAnswer) VALUES
('Physics', 'Light', 'What is the speed of light in vacuum?', '["3 × 10^8 m/s", "3 × 10^6 m/s", "3 × 10^5 m/s", "3 × 10^7 m/s"]', '3 × 10^8 m/s', 'Light travels at approximately 3 × 10^8 meters per second in vacuum.'),
('Physics', 'Light', 'Which phenomenon explains the bending of light when it passes from one medium to another?', '["Reflection", "Refraction", "Dispersion", "Diffraction"]', 'Refraction', 'Refraction occurs due to change in speed of light in different media.'),
('Physics', 'Light', 'Which mirror is used in car headlights?', '["Concave", "Convex", "Plane", "Parabolic"]', 'Concave', 'Concave mirrors focus light into a beam, useful in headlights.'),
('Physics', 'Light', 'What is the focal length of a plane mirror?', '["Zero", "Infinite", "Equal to radius", "Cannot be determined"]', 'Infinite', 'Plane mirrors have an infinite focal length as they do not converge light.'),
('Physics', 'Light', 'Which lens is used to correct myopia?', '["Convex", "Concave", "Cylindrical", "Bifocal"]', 'Concave', 'Concave lenses diverge light rays and help correct short-sightedness.'),
('Physics', 'Light', 'What is the unit of power of a lens?', '["Dioptre", "Joule", "Watt", "Candela"]', 'Dioptre', 'Lens power is measured in dioptres (D), reciprocal of focal length in meters.'),
('Physics', 'Light', 'Which color of light bends the most during dispersion?', '["Red", "Green", "Blue", "Violet"]', 'Violet', 'Violet light has the shortest wavelength and bends the most.'),
('Physics', 'Light', 'What type of image is formed by a convex mirror?', '["Real and inverted", "Virtual and erect", "Real and erect", "Virtual and inverted"]', 'Virtual and erect', 'Convex mirrors always form virtual, erect, and diminished images.'),
('Physics', 'Light', 'Which device splits white light into its constituent colors?', '["Mirror", "Lens", "Prism", "Grating"]', 'Prism', 'A prism disperses white light into its spectrum due to refraction.'),
('Physics', 'Light', 'What is the magnification of a plane mirror?', '["0", "1", "2", "Infinity"]', '1', 'Plane mirrors produce images of the same size, so magnification is 1.');
INSERT INTO question_bank (subject, topic, questionText, options, correctAnswer) VALUES
('Physics', 'Electricity', 'What is the SI unit of electric current?', '["Ampere", "Volt", "Ohm", "Watt"]', 'Ampere', 'Electric current is measured in amperes (A).'),
('Physics', 'Electricity', 'What does Ohm\'s law state?', '["V = IR", "P = VI", "I = V/R", "R = V/I"]', 'V = IR', 'Ohm\'s law relates voltage, current, and resistance: V = IR.'),
('Physics', 'Electricity', 'What is the resistance of a conductor if 2 A current flows through it at 10 V?', '["5 Ω", "10 Ω", "20 Ω", "0.2 Ω"]', '5 Ω', 'Using Ohm\'s law: R = V/I = 10/2 = 5 Ω.'),
('Physics', 'Electricity', 'Which device measures electric current?', '["Voltmeter", "Ammeter", "Galvanometer", "Multimeter"]', 'Ammeter', 'An ammeter is used to measure current in a circuit.'),
('Physics', 'Electricity', 'What is the unit of electrical energy?', '["Joule", "Watt", "Kilowatt-hour", "Ampere"]', 'Kilowatt-hour', 'Electrical energy is commonly measured in kilowatt-hours (kWh).'),
('Physics', 'Electricity', 'What is the power consumed by a 100 W bulb in 5 hours?', '["500 W", "500 J", "500 Wh", "500 kWh"]', '500 Wh', 'Power × time = 100 W × 5 h = 500 Wh.'),
('Physics', 'Electricity', 'Which material is a good conductor of electricity?', '["Rubber", "Plastic", "Copper", "Wood"]', 'Copper', 'Copper is widely used due to its excellent conductivity.'),
('Physics', 'Electricity', 'What is the effect of increasing resistance in a circuit?', '["Current increases", "Voltage increases", "Current decreases", "Power increases"]', 'Current decreases', 'Higher resistance reduces current flow according to Ohm\'s law.'),
('Physics', 'Electricity', 'What is the function of a fuse in an electric circuit?', '["Increase current", "Prevent overheating", "Store energy", "Convert energy"]', 'Prevent overheating', 'A fuse breaks the circuit when current exceeds safe limits.'),
('Physics', 'Electricity', 'What is the formula for electrical power?', '["P = VI", "P = V/I", "P = I/V", "P = IR"]', 'P = VI', 'Electrical power is the product of voltage and current.');
INSERT INTO question_bank (subject, topic, questionText, options, correctAnswer) VALUES
('Physics', 'Magnetic Effects of Electric Current', 'Which device converts electrical energy into mechanical energy?', '["Motor", "Generator", "Transformer", "Battery"]', 'Motor', 'An electric motor converts electrical energy into mechanical energy.'),
('Physics', 'Magnetic Effects of Electric Current', 'What is the direction of magnetic field around a current-carrying wire?', '["Parallel to wire", "Perpendicular to wire", "Circular around wire", "Opposite to current"]', 'Circular around wire', 'The magnetic field forms concentric circles around the wire.'),
('Physics', 'Magnetic Effects of Electric Current', 'Which rule helps determine the direction of magnetic field around a conductor?', '["Right-hand thumb rule", "Fleming\'s left-hand rule", "Lenz\'s law", "Faraday\'s law"]', 'Right-hand thumb rule', 'Right-hand thumb rule gives the direction of magnetic field around a current-carrying wire.'),
('Physics', 'Magnetic Effects of Electric Current', 'What is the function of a commutator in a motor?', '["Increase voltage", "Reverse current direction", "Store charge", "Reduce resistance"]', 'Reverse current direction', 'Commutator reverses current direction to maintain rotation in one direction.'),
('Physics', 'Magnetic Effects of Electric Current', 'Which material is used to make electromagnets?', '["Copper", "Aluminium", "Soft iron", "Steel"]', 'Soft iron', 'Soft iron enhances magnetic field and loses magnetism quickly when current stops.'),
('Physics', 'Magnetic Effects of Electric Current', 'What is the unit of magnetic field?', '["Tesla", "Gauss", "Weber", "Ampere"]', 'Tesla', 'Magnetic field strength is measured in Tesla (T).'),
('Physics', 'Magnetic Effects of Electric Current', 'Which rule is used to find the direction of force on a current-carrying conductor in a magnetic field?', '["Right-hand thumb rule", "Fleming\'s left-hand rule", "Lenz\'s law", "Faraday\'s law"]', 'Fleming\'s left-hand rule', 'Fleming\'s left-hand rule gives the direction of force, field, and current.'),
('Physics', 'Magnetic Effects of Electric Current', 'What happens to magnetic field strength if current increases?', '["Increases", "Decreases", "Remains same", "Becomes zero"]', 'Increases', 'Magnetic field strength is directly proportional to current.'),
('Physics', 'Magnetic Effects of Electric Current', 'Which part of the motor rotates?', '["Armature", "Commutator", "Brushes", "Field coil"]', 'Armature', 'Armature is the rotating part of the motor where current flows.'),
('Physics', 'Magnetic Effects of Electric Current', 'Which device converts mechanical energy into electrical energy?', '["Motor", "Generator", "Transformer", "Capacitor"]', 'Generator', 'Generators convert mechanical energy into electrical energy using electromagnetic induction.');
